import React from 'react';
import {addMonths,subMonths,startOfWeek,addDays,format,isSameMonth,isSameDay,endOfWeek,startOfMonth,endOfMonth,parse} from 'date-fns';
import './calendar.css';
import {useState}  from 'react';
cosnt [state,setState] = useState({currentMonth: new Date(),
    selectedDate: new Date(),
    currentMonth: new Date(),
    selectedDate: new Date()
})
const nextMonth = () => {
  setState({
   currentMonth: addMonths(this.state.currentMonth, 1)
 });
};
const prevMonth = () => {
   setState({
   currentMonth: subMonths(this.state.currentMonth, 1)
 });
}; 
const onDateClick = day => {
      setState({
      selectedDate: day
    });
  };

const Calendar = props =>{

   const Header = () =>{
       const dateFormat = "MMMM YYYY";
       return (
        <div className="header row flex-middle">
          <div className="col col-start">
            <div className="icon" onClick={prevMonth}>
              chevron_left
            </div>
          </div>
          <div className="col col-center">
            <span>
              {format(state.currentMonth, dateFormat)}
            </span>
          </div>
          <div className="col col-end" onClick={nextMonth}>
            <div className="icon">chevron_right</div>
          </div>
        </div>
      );
   }
   const Days = () =>{
    const dateFormat = "dddd";
    const days = [];
    let startDate = startOfWeek(state.currentMonth);
    for (let i = 0; i < 7; i++) {
      days.push(
        <div className="col col-center" key={i}>
          {format(addDays(startDate, i), dateFormat)}
        </div>
      );
    }

    return <div className="days row">{days}</div>;
   }

    const Cells = () =>{
const { currentMonth, selectedDate } = this.state;
const monthStart = startOfMonth(currentMonth);
const monthEnd = endOfMonth(monthStart);
const startDate = startOfWeek(monthStart);
const endDate = endOfWeek(monthEnd);
    const dateFormat = "D";
const rows = [];
let days = [];
let day = startDate;
let formattedDate = "";
while (day <= endDate) {
  for (let i = 0; i < 7; i++) {
    formattedDate = format(day, dateFormat);
    const cloneDay = day;
    days.push(
      <div
        className={`col cell ${
          !isSameMonth(day, monthStart)
            ? "disabled"
            : isSameDay(day, selectedDate) ? "selected" : ""
        }`}
        key={day}
        onClick={() => onDateClick(parse(cloneDay))}
      >
        <span className="number">{formattedDate}</span>
        <span className="bg">{formattedDate}</span>
      </div>
    );
    day = addDays(day, 1);
  }
  rows.push(
    <div className="row" key={day}>
      {days}
    </div>
  );
  days = [];

}
return <div className="body">{rows}</div>;
}

return (<div className = "calendar">
             {Header}
             {Days}
             {Cells}
          </div>);

};

 export default Calendar;
