import {React,useState,useEffect} from 'react';
import { useHttpClient } from '../../shared/hooks/http-hook';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import { useParams } from 'react-router-dom';
import Button from '../../shared/components/FormElements/Button';
import Card from '../../shared/components/UIElements/Card';
import MovieDescription from'./MovieDescription';

  const Description = props => {
  let movieid = useParams().movieId;
  const [LoadedDes, setLoadedDes] = useState();
  const [isLoading,setisLoading] = useState(false);
  const [error, setError] = useState();
  console.log(movieid);
  useEffect(() => {

     const fetchDes = async () => {
      setisLoading(true);
      try{
        const response= await fetch(`http://localhost:5000/api/movies/${movieid}`);
        const responseData = await response.json();
        if(!response.ok){
          throw new Error(responseData.message);
        }
        setLoadedDes(responseData.movie);

      } catch (err) {
        setError(err.message);
      }
      setisLoading(false);
    };

    fetchDes();
  },[movieid]);
  const errorHandler = () =>{
    setError=(null);
  }

return (
  <MovieDescription
  name = {LoadedDes.Title}
  image={LoadedDes.Poster}
  length = {LoadedDes.Runtime}
  rating={LoadedDes.Rated}
  genre={LoadedDes.Genre}
  description={LoadedDes.Plot}/>)


};


export default Description;
