import React from 'react';
import ReactDOM from 'react-dom';
import './Modal.css';
const ModalOverlay = props => { 
    const content = (
      <div className = "modal">
        <header>
          <h2>{props.header}</h2>
        </header>
        <form>
          <div >
            {props.children}
          </div>
          <footer>
            {props.footer}
          </footer>
        </form>

      </div>
    );
    return ReactDOM.createPortal(content, document.getElementById('modal-hook'));
  };
  export default ModalOverlay;
