import Button from '../../shared/components/FormElements/Button';
import Modal from '../../shared/components/UIElements/Modal';
import Map from '../../shared/components/UIElements/Map';
import React, { useState, useContext } from 'react';
const SingleplaceItem = props => {

  const [showMap, setShowMap] = useState(false);
  const openMapHandler = () => setShowMap(true);
  const closeMapHandler = () => setShowMap(false);

return(
  <React.Fragment>
     <Modal show = {showMap} onCancel = {closeMapHandler} header = {props.address}
       contentClass="place--item__modal-content" footerClass="place-item__modal-actions"
       footer ={<Button onClick = {closeMapHandler}>CLOSE</Button>}>
       <div className="map-container">
       <h2>THE MAP!
         </h2>

       </div>   
     </Modal>
    <div className="card mb-3" style={{maxwidth: "540px"}}>
        <div className="row no-gutters">
          <div className="col-md-4">  
          <img src= {props.image} className="card-img" />
          </div>
          <div className="col-md-8">
            <div className="card-body">
              <h1 className="card-title">{props.name}</h1>
              <p className="card-text">{props.description}</p>
              <Button className="b" onClick={openMapHandler}>VIEW ON MAP</Button>
              <Button className="b">Browse Movies Here</Button>
            </div>
          </div>
        </div>

      </div>
      </React.Fragment>

     )
};
export default SingleplaceItem;
