import React, { useEffect,useState } from 'react';
import MovieList from '../components/MovieList';
import { useHttpClient } from '../../shared/hooks/http-hook';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import Pagination from '../../shared/components/UIElements/pagination';
const Movies = () => {
  const[Posts,setPosts] = useState([]);
  const[LoadedMovies,setLoadedMovies] = useState();
  const[isLoading,setisLoading] = useState(false);
  const[error,setError] = useState();
  const[postsPerPage] = useState(20);
  const[currentPage,setcurrentPage] = useState(1);
   let indexOfLastPosts;
  let indexOfFirstPost;

  let currentPosts;
 let paginate;

  useEffect(()=>{

    const fetchMovies = async()=>{
      setisLoading(true);
      try{          
        const response= await fetch(`http://localhost:5000/api/movies`); 
        const responseData = await response.json();
        if(!response.ok){
          throw new Error(responseData.message);
        }
        setLoadedMovies(responseData.movies);

      } catch (err) {
        setError(err.message);
      }
      setisLoading(false);
    };

    fetchMovies();
  },[]);
  const errorHandler = () =>{
    setError=(null);
  }
  indexOfLastPosts = currentPage * 20;
        indexOfFirstPost = indexOfLastPosts - postsPerPage;

         currentPosts = LoadedMovies.slice(indexOfFirstPost,indexOfLastPosts);
         paginate = pageNumber => setcurrentPage(pageNumber);



  return(       
    <React.Fragment>
    {isLoading && (<div><LoadingSpinner /></div>)}
    {!isLoading && LoadedMovies&&<MovieList items={currentPosts}/>&&<Pagination postsPerPage = {postsPerPage} totalPosts={LoadedMovies.length} paginate={paginate}/>}

    </React.Fragment>)
  };

export default Movies;
