import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import Card from '../../shared/components/UIElements/Card';
import Button from '../../shared/components/FormElements/Button';

import './ShowTimeItem.css';


const ShowTimeItem = props => {
  return (
    <div className="card card1 mb-3" style={{maxwidth: "540px"}}>
    <div className="row no-gutters">
      <div className="col-md-4">  
      <img src= {props.image} className="card-img" />
      </div>
      <div className="col-md-8">
        <div className="card-body">
          <h2 className="card-title">{props.title}</h2>
          <div className="choosetime">{props.time.map(t => <span className="choosetime1"><p>{t.theatre.name}</p><Link><Button>{props.time}</Button></Link></span>)}
         </div>
        </div>
      </div>
    </div>

  </div>

  );
};

export default ShowTimeItem;
