    if(!response.ok){
      throw new Error(responseData.message);
    }
    setLoadedDescription(responseData.movie);

  } catch (err) {
    setError(err.message);
  }
  setisLoading(false);
};

fetchDescription();
},[movieId]);

const errorHandler = () =>{
setError=(null);
}


  return (<React.Fragment>
    {isLoading && (<div>
  <LoadingSpinner />
</div>)}  
{!isLoading && LoadedDescription &&<MovieDescription 
  name = {LoadedDescription.Title} 
  image={LoadedDescription.Poster}
  length = {LoadedDescription.Runtime}
  rating={LoadedDescription.Rated}
  genre={LoadedDescription.Genre}
  description={LoadedDescription.Plot}/>}
  </React.Fragment>)

};


export default Description;
