import React from 'react';
import Weekday from './weekday';
import moment from 'moment';
import './calendar.css';
let state = {
    dateObject: moment()
}

const firtsDayOfMonth = () =>{
    let dateObject = state.dateObject;
    let firstDay = moment(dateObject).startOf("month").format("d");
    return firstDay;
};
const daysInMonth = () =>{
    let dateObject = state.dateObject;
    let days = moment(dateObject).daysInMonth().format("d");;
    return days;
}
const Calendar = props =>{
     let blanks = [];
     for(let i = 0; i < this.firtsDayOfMonth();i++) {
         blanks.push(<td className="calendar-day empty">{""}</td>)
     }
     let daysInMonth=[];
     for(let d = 1; d <= this.daysInMonth(); d++) {
     daysInMonth.push(<td key={d} className="calendar-day">{d}</td>)
     }
     var totalSlots = [...blanks,...daysInMonth];
     let rows = [];
     let cells = [];
     totalSlots.forEach((row,i)=>{
         if(i%7 !==0) {
             cells.push(row);
         }else{
             rows.push(cells);
             cells = [];
             cells.push(row);
         }
         if(i===totalSlots.length-1){
             rows.push(cells);
         }
     })
      return (
          <table>
              <thead className = "calendar-day">
              <tr><Weekday /></tr>
              </thead>
             <tbody>{daysinmonth}</tbody>
          </table>


      );

};

 export default Calendar;
