import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import Card from '../../shared/components/UIElements/Card';
import Button from '../../shared/components/FormElements/Button';

import './ShowTimeItem.css';


const ShowTimeItem = props => {
  return (
    <div className="box">
    <div className="row no-gutters">
      <div>  
      <img src= {props.image} className="mg" />
      </div>
      <div>
           <div >
           <h6 className="card--title">{props.title}</h6>
           <p className="content">Summary:     {props.des}</p>
           <p className="content">Location{props.theatre}</p>
           <Button className="gettime">{props.time}</Button>
            </div>
        </div>
      </div>
    </div>


  );
};

export default ShowTimeItem;
