import React from 'react';
import {addMonths,subMonths,startOfWeek,addDays,format,isSameMonth,isSameDay,endOfWeek,startOfMonth,endOfMonth,parse} from 'date-fns';
import './calendar.css';
import {useState}  from 'react';
import Calendar from 'react-calendar';

class Cal extends Component {
    state = {
      date: new Date(),
    }

    onChange = date => this.setState({ date })

    render() {
      return (
        <div>
          <Calendar
            onChange={this.onChange}
            value={this.state.date}
          />
        </div>
      );
    }
  }
